  public static getSvg(t): string {
    const me = this;
    let style = "";
    let attr = "";
    let idx = 0;
    if (me.svgAttr[t] !== undefined) {
      for (var j in me.svgAttr[t]) {
        if (me.svgAttr[t].hasOwnProperty(j)) {
          var fn = me.svgAttr[t][j];
          if (idx > 0) {
            attr += " ";
          }
          attr += j + '="' + fn + '"';
        }
        idx++;
      }
    }
    //debugger;
    idx = 0;
    if (me.svgStyle[t] !== undefined) {
      for (var j in me.svgStyle[t]) {
        if (me.svgStyle[t].hasOwnProperty(j)) {
          var fn = me.svgStyle[t][j];
          if (idx > 0) {
            style += "; ";
          }
          style += j + ":" + fn;
        }
        idx++;
      }
    }
    return (
      "<svg " +
      (style === "" ? "" : 'style="' + style + '"') +
      " " +
      attr +
      ">" +
      me.systemIconType[t] +
      "</svg>"
    );
  }
